tempdf <- read.csv("data/RadiosondeMetO/JustTEMPLines.txt", header=FALSE, col.names=c('MSG_TYPE','ROWS1','ROWS2','ROWS3','YEAR','MNTH','DAY','HOUR','MINT','RCPT_YEAR','RCPT_MNTH','RCPT_DAY','RCPT_HOUR','RCPT_MINT','RCPT_YEAR_PARTA','RCPT_MNTH_PARTA','RCPT_DAY_PARTA','RCPT_HOUR_PARTA','RCPT_MINT_PARTA','RCPT_YEAR_PARTB','RCPT_MNTH_PARTB','RCPT_DAY_PARTB','RCPT_HOUR_PARTB','RCPT_MINT_PARTB','RCPT_YEAR_PARTC','RCPT_MNTH_PARTC','RCPT_DAY_PARTC','RCPT_HOUR_PARTC','RCPT_MINT_PARTC','RCPT_YEAR_PARTD','RCPT_MNTH_PARTD','RCPT_DAY_PARTD','RCPT_HOUR_PARTD','RCPT_MINT_PARTD','CALL_SIGN','WMO_BLCK_NMBR','WMO_STTN_NMBR','LTTD','LNGD','STTN_HGHT','COLTN_CNTR','BLTN_IDNY','RPRT_IDNY','PESR_SNSR_HGHT','RADI_SNDE_TYPE','TRCKG_SYTM','RADTN_CORTN','SEA_SRFC_TMPR','MXMM_WIND_LEVL_WIND_SHER1','MXMM_WIND_LEVL_WIND_SHER2','CLOD_AMNT_1','CLOD_TYPE_1','CLOD_BASE_HGHT_1','LOD_AMNT_2','CLOD_TYPE_2','CLOD_BASE_HGHT_2','LOD_AMNT_3','CLOD_TYPE_3','CLOD_BASE_HGHT_3','LEVL_CDTN_CODE'))
res <- subset(tempdf, select=c('YEAR', 'MNTH', 'DAY', 'HOUR', 'MINT', 'RADI_SNDE_TYPE', 'TRCKG_SYTM', 'RADTN_CORTN', 'WMO_BLCK_NMBR', 'WMO_STTN_NMBR'))
res$WMO_ID = paste(as.character(res$WMO_BLCK_NMBR), as.character(res$WMO_STTN_NMBR), sep='')
res$MINT[res$MINT == -9999999] = 0
str_timestamp = paste(res$YEAR, res$MNTH, res$DAY, res$HOUR, res$MINT, sep='-')
res$timestamp  = strptime(str_timestamp, "%Y-%m-%d-%H-%M")
res <- subset(res, select=c('WMO_ID', 'timestamp', 'RADI_SNDE_TYPE', 'TRCKG_SYTM', 'RADTN_CORTN'))
